#include <iostream>
#include <string>
using namespace std;

int main()
{
	string n;
	cin >> n;
	string ans;
	int sum = 0;
	for (size_t i = 0; i < n.size(); i++)
		sum += n[i] - 48;
	if (sum % 3 == 0)
		ans += "Fizz";
	if (n.back() == '0' || n.back() == '5')
		ans += "Buzz";
	if (ans.empty())
		ans = n;
	cout << ans << endl;
}
