#include <iostream>
#include <algorithm>
#include <cmath>
#include <string>
#include <vector>
#include <queue>
typedef unsigned long long ULL;

 int main (){

	 std::string str;

	 std::getline(std::cin, str);

	 for (int i = 0; i < str.size(); ++i) {
		 if (str[i] == ' ' || str[i] == '.') {
			 continue;
		 }
		 else {
			 str[i] -= 1;
			 if (str[i] == 123) {
				 str[i] = 'a';
			 }
		 }
	 }

	 std::cout << str << std::endl;
	 return 0;

}
