#include<iostream>
#include<vector>
int main() {
	int n, m,sum=0;
	std::cin >> n >> m;
	std::vector<int> a,b;
	for (int i = 0; i < n; i++) {
		int x;
		std::cin >> x;
		a.push_back(x);
		b.push_back(i);
			for (int j = 0; j < a.size()-1; j++) {
				if (a[i - j] < a[i - j - 1]) {
					x = a[i - j];
					a[i - j] = a[i - j - 1];
					a[i - j - 1] = x;
					x = b[i - j];
					b[i - j] = b[i - j - 1];
					b[i - j - 1] = x;
				}
			}
	}
	for (int i = 0; i < n; i++) {
		if (b[i] * a[i] < m) {
			sum += b[i];
			m -= a[i]*b[i];
		}
		else{
			sum += m / a[i];
		}
	}
	std::cout << sum << std::endl;
	std::cin >> m;
}
