#include <iostream>
#include <string>
using namespace std;
int need = 0;
int gyudon = 0;
int kosuu[10001] = {};
int Yen[10001] = {};
long long int needmoney[10001] = {};
long long int answer = 0;
int j = 0;
int k = 0;
int l = 0;

void hikaku(int i) {
	if (i == 1) {
		answer = needmoney[1];
	}
	if (i >= 2) {
		if (needmoney[i] > needmoney[i - 1]) {
			answer = needmoney[i - 1];
		}
		if (needmoney[i] < needmoney[i - 1]) {
			answer = needmoney[i];
		}
	}
}

int main() {
	cin >> need >> gyudon;
	for (int i = 1; i <= gyudon; i++) {
		cin >> kosuu[i] >> Yen[i];
		if (need%kosuu[i] == 0) {
			needmoney[i] = Yen[i] * (need / kosuu[i]);
		}
		if(need%kosuu[i] != 0){
			j = need / kosuu[i];
			k = (need%kosuu[i]);
			l = j + k;
			needmoney[i] = Yen[i] * l;
		}
		hikaku(i);
	}
	cout << answer << '\n';
}
