#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;
int main() {
	int n;
	unsigned long long   m;
	m = 1;
	cin >> n;
	for (int  i=0; i < n; i++) {
		m = m * 2;
	}
	unsigned long long q = m - 1;


	cout << q % 1000000007 << endl;
}
