#include <iostream>
#include<string>
using namespace std;

int main()
{
   string s;
   int64_t n=0;
   bool b=0,f=0;
   cin>>s;
   for(int i=s.size()-1;i>=0;--i){
     if(i==s.size()-1){
       if(s[i]=='0'||s[i]=='5'){
         b=true;
       }
     }
     n+=stoi(s);
   }
   if(n%3==0){
     cout<<"Fizz";
     f=true;
   }
   if(b){
     cout<<"Buzz";
     f=true;
   }
   
   if(!f){
     cout<<s;
     }
   cout<<endl;
	return 0;
}
