#include <iostream>
#include <vector>
int main() {
	int N, M;
	std::cin >> N >> M;
	std::vector <int> vec;
	std::vector <int> vec2;
	vec2 = vec;
	for (int i = 0; i < N; ++i) {
		int x;
		std::cin >> x;
		vec.push_back(x);
	}
	for (int j = 0; j < 100; ++j) {
		for (int i = 0; i < N - 1; ++i) {
			if (vec[i] > vec[i + 1]) {
				int x = vec[i];
				vec[i] = vec[i + 1];
				vec[i + 1] = x;
			}
		}
	}
	vec2 = vec;
	for (int i = 0; i < vec.size(); ++i)
		std::cout << vec[i] << " ";
	int min = M, kabu = 0;
	for (int i = 0; i < vec2.size(); ++i) {
		for (int j = 0; j < i; ++j) {
			if (min >= vec2[j]) {
				kabu += 1;
			}
		}
	}
	kabu += 1;
	std::cout << kabu << std::endl;
	system("pause");
}
