#include <utility>
#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
#include <map>
#include <cmath>
#include <numeric>
#include <limits>
#define ll long long
#define rep(i,rept) for (ll i = 0; i < rept; i++)
#define Yes(b) std::cout<< (b ? "Yes" : "No")<<std::endl
#define YES(b) std::cout<< (b ? "YES" : "NO")<<std::endl
#define fr first
#define se second
using namespace std;

int main() {
	ll n, k = 0;
	ll a[100000], b[100000];
	cin >> n;
	rep(i, n) {
		cin >> a[i] >> b[i];
	}
	sort(a, a + n);
	sort(b, b+ n);
	rep(i, n) {
		k += a[i] * b[n - i - 1];
	}
	cout << k << endl;
}
