#include <iostream>
#include <string>
using namespace std;

int main()
{
	string s;
	cin >> s;
	int d = 0;
	for (size_t i = 0; i < s.size(); i++)
	{
		switch (s[i])
		{
		case 'L':
			d++;
			break;
		case 'R':
			d--;
			break;
		default:
			break;
		}
	}
	cout << (d % 4 != 0 ? "YES" : "NO") << endl;
}
