#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;
int main() {
	long long n, m;
	long long q[100000];
	cin >> n >> m;
	for (long long i = 0; i < m; i++) {
		long long a, b;
		cin >> a >> b;
		int o;
		//セット数の判定
		if (n % a == 0) {
			o = n / a;
		}

		else {
			o= n / a + 1;
		}
		long long y = a * b*o;
		q[i] = y;
	}

	long long e = q[0];
	for (long long i = 0; i < m; i++) {
		long long u = q[i];
		if (e > u) {
			e = u;
		}
	
	}
	cout << e << endl;
}
