#include <iostream>
#include <string>
using namespace std;

int main()
{
	string s;
	cin >> s;
	int d = 0, x = 0, y = 0;
	for (size_t i = 0; i < s.size(); i++)
	{
		switch (s[i])
		{
		case 'L':
			if (d == 3)
				d -= 4;
			d++;
			break;
		case 'R':
			if (d == 0)
				d += 4;
			d--;
			break;
		case 'S':
			switch (d)
			{
			case 0:
				y++;
				break;
			case 1:
				x--;
				break;
			case 2:
				y--;
				break;
			case 3:
				x++;
				break;
			}
			break;
		}
	}
	cout << (x == 0 && y == 0 || d % 4 != 0 ? "YES" : "NO") << endl;
}
