#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll ans[20010];
ll n, m, a[20010], s[20010], t[20010], v[20010], k[20010];

int main() {
	cin >> n >> m;
	for (int i = 0;i < m;i++) {
		cin >> a[i];
		if (a[i] == 1) {
			cin >> s[i] >> t[i] >> v[i];
		}
		else {
			cin >> k[i];
		}
	}
	for (int i = 0;i < m;i++)ans[i] = -1;
	for (int i = m - 1;i >= 0;i--) {
		if (a[i] == 2) {
			for (int j = i - 1;j >= 0;j--) {
				if (a[j] == 2)continue;
				if (s[j] <= k[i] && k[i] < t[j]) {
					ans[i] = v[j];
					break;
				}
			}
			if (ans[i] == -1)ans[i] = 0;
		}
	}
	for (int i = 0;i < m;i++) {
		if (ans[i] == -1)continue;
		cout << ans[i] << endl;
	}
	return 0;
}
