#include<iostream>
#include<algorithm>
constexpr int H_MAX=1000;
constexpr int W_MAX=1000;
constexpr int dx[8]={1,1,1,-1,-1,-1,0,0};
constexpr int dy[8]={1,0,-1,1,0,-1,1,-1};
int arr[H_MAX+2][W_MAX+2];
int main(){
	int H,W,X,tgH,tgW,ans[8];
	std::cin>>H>>W>>X;
	for(int i=1;i<=H;i++){
		for(int j=1;j<=W;j++){
			arr[i][j]=j+(i-1)*W;
		}
	}
	for(int i=1;i<=H;i++){
		arr[i][0]=arr[i][W];
	}
	for(int i=1;i<=H;i++){
		arr[i][W+1]=arr[i][1];
	}
	for(int i=1;i<=W;i++){
		arr[0][i]=arr[H][i];
	}
	for(int i=1;i<=W;i++){
		arr[H+1][i]=arr[1][i];
	}
	arr[0][0]=arr[H][W];
	arr[H+1][W+1]=arr[1][1];
	arr[0][W+1]=arr[H][1];
	arr[H+1][0]=arr[1][W];
	for(int i=1;i<=H;i++){
		for(int j=1;j<=W;j++){
			if(arr[i][j]==X){
				tgH=i;
				tgW=j;
			}
		}
	}
	for(int i=0;i<8;i++){
		ans[i]=arr[tgH+dy[i]][tgW+dx[i]];
	}
	std::sort(ans,ans+8);
	for(int i=0;i<8;i++){
		std::cout<<ans[i];
		if(i-7)std::cout<<" ";
	}
	std::cout<<"\n";
}
