#include<iostream>
#include<algorithm>
#include<vector>
#include<string>

class trie {
public:
	int data;
	trie *next[6];
	trie() {
		data = 0;
		std::fill(next, next + 6, (trie*)NULL);
	}
	void insert(char *s) {
		if (*s == '\0') {
			next[5] = new trie();
			return;
		}
		if (next[*s - 'a'] == NULL) {
			next[*s - 'a'] = new trie();
		}
		next[*s - 'a']->data++;
		next[*s - 'a']->insert(s + 1);
	}
	bool find(char *s) {
		if (*s == '\0')return next[5] != NULL;
		if (next[*s - 'a'] == NULL)return false;
		return next[*s - 'a']->find(s + 1);
	}
	int count(char *s) {
		if (next[*s - 'a'] == NULL)return 0;
		if (*(s + 1) == '\0')return next[*s - 'a']->data;
		return next[*s - 'a']->count(s + 1);
	}
};
int main() {
	trie T;
	int M;
	std::cin >> M;
	for (int i = 0; i < M; i++) {
		int P;
		std::string S;
		std::cin >> P >> S;
		if (P == 0) {
			T.insert(&S[0]);
		}
		if (P == 1) {
			std::cout << (T.find(&S[0]) ? "Yes" : "No") << std::endl;
		}
		if (P == 2) {
			std::cout << T.count(&S[0]) << std::endl;
		}
	}
	return 0;
}
