#include<iostream>
#include<algorithm>
#include<vector>
#define LL long long
LL dp[2000][40010];
LL A[2010], B[2010];
LL N, M;
LL ans;
int main() {
	std::cin >> N >> M;
	for (int i = 0; i<N; i++) {
		std::cin >> A[i] >> B[i];
	}
	for (int i = 0; i < N; i++) {
		for (int j = 1; j < 40010; j++) {
			dp[i][j] = 1e17;
		}
	}
	for (int i = 0; i<N; i++) {
		for (int j = 0; j < 40010; j++) {
			if (dp[i][j] + A[i] <= M) {
				dp[i + 1][j + B[i]] = dp[i][j] + A[i];
				ans = std::max(ans, j + B[i]);
			}
		}
	}
	std::cout << ans << std::endl;
	return 0;
}
