#include <iostream>
#include <algorithm>
#include <cmath>
#include <string>
#include <vector>
#include <queue>
typedef unsigned long long ULL;

 int main (){

	 std::string str;

	 std::cin >> str;

	 for (int i = 0; i < str.size(); ++i) {
		 if (str[i] > 96) {
			 str[i] -= 32;
		 }
		 else {
			 str[i] += 32;
		 }
	 }

	 std::cout << str << std::endl;
	 return 0;
}
