#include "iostream"
#include "string"
#include "vector"
#include "map"
#include "queue"
#include "stack"
#include "algorithm"

using namespace std;

int main() {
	string s;
	cin >> s;
	int tmp = 'A' - 'a';
	for (int i = 0; i < s.size(); i++) {
		if (s[i] <= 'z' && s[i] >= 'a') s[i] += tmp;
		else if (s[i] <= 'Z' && s[i] >= 'A') s[i] -= tmp;
	}
	cout << s << endl;

	return 0;
}
