#include<iostream>
#include<string>

std::string str;

int main(){
    
    std::cin>>str;
    
    for(auto&& x:str){
        if(x>='A'&&x<='Z')
            x+='a'-'A';
        else
        if(x>='a'&&x<='z')
            x-='a'-'A';
    }
    
    std::cout<<str<<std::endl;
    
}
