#include <iostream>
#include <string>
int main(){
	std::string str;
	std::cin>>str;
	for(int i=0;i<str.size();i++){
		if('a'<=str[i]&&str[i]<='z'){
			str[i]+='A'-'a';
		}
		else
		if('A'<=str[i]&&str[i]<='Z'){
			str[i]+='a'-'A';
		}
	}
	std::cout<<str<<std::endl;
	return 0;
}
