#include<iostream>
#include<string>

int main() {
	std::string s;
	std::cin >> s;
	for (int i = 0; i < s.size(); i++) {
		if (s[i] == 'Z') {
			s[i] = 'z';
		}
		else if (s[i] <= 'A', s[i] >= 'Z') {
			s[i] -= 'a' - 'A';
		}
		else {
			s[i] += 'a' - 'A';
		}
	}
	std::cout << s << std::endl;
}
