#include <iostream>
#include <string>
int main() {
	std::string str;
	std::cin >> str;
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] >= 97 && str[i] <= 122) {
			str[i] -= 32;
		}
		else if (str[i] >= 65 && str[i] <= 90) {
			str[i] += 32;
		}
	}
	std::cout << str << std::endl;
	system("pause");
	return 0;
}
