#include<iostream>
#include<vector>

std::vector<int> vec;

int n;

bool asc(int& left,int& right){
    return left%2==right%2 ? left<right : left%2;
}

int main(){
    
    int a;
    
    std::cin>>n;
    
    for(int i=0;i<n;i++){
        std::cin>>a;
        vec.push_back(a);
    }
    
    std::sort(vec.begin(),vec.end(),asc);
    
    for(auto&& x:vec)
    std::cout<<x<<std::endl;
    
}
