#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, m, a[2010], b[2010], ans;
ll dp[40010];

int main() {
	cin >> n >> m;
	for (int i = 0;i < n;i++) {
		cin >> a[i] >> b[i];
	}
	fill(dp, dp + 40005, INF2);
	dp[0] = 0;
	for (int i = 0;i < n;i++) {
		for (int j = 40005;j >= 0;j--) {
			if (dp[j] != INF2) {
				if (j + b[i] > 40000)continue;
				dp[j + b[i]] = min(dp[j + b[i]], dp[j] + a[i]);
			}
		}
	}
	for (ll i = 0;i < 40005;i++) {
		if (dp[i] <= m) {
			ans = max(ans, i);
		}
	}
	cout << ans << endl;
	return 0;
}
