#include <iostream>
#include <vector>
int main() {
	int N;
	std::cin >> N;
	std::vector <int> vec;
	for (int i = 0; i < N; ++i) {
		int x;
		std::cin >> x;
		vec.push_back(x);
	}
	std::vector <int> vec2;
	for (int i = 0; i < vec.size(); ++i) {
		if (vec[i] % 2 == 1)
			vec2.push_back(vec[i]);
	}
	for (int i = 0; i < vec.size(); ++i) {
		if (vec[i] % 2 == 0)
			vec2.push_back(vec[i]);
	}
	for (int i = 0; i < vec2.size(); ++i) {
		std::cout << vec2[i] << " ";
		if (i == vec2.size() - 1)
			std::cout << std::endl;
	}
	system("pause");
	return 0;
}
