#include<iostream>
#include<algorithm>
#include<vector>
#define LL long long
LL dp[1010][10010];
LL A[1010], B[1010];
LL N, M;
LL ans;
int main() {
	std::cin >> N >> M;
	for (int i = 0; i<N; i++) {
		std::cin >> A[i] >> B[i];
	}
	for (int i = 0; i < N; i++) {
		for (int j = 1; j < 10010; j++) {
			dp[i][j] = 1e17;
		}
	}
	for (int i = 0; i<N; i++) {
		for (int j = 0; j < 10010; j++) {
			dp[i + 1][j] = std::min(dp[i + 1][j], dp[i][j]);
			if (dp[i][j] + A[i] <= M) {
				dp[i + 1][j + B[i]] = std::min(dp[i][j] + A[i], dp[i + 1][j + B[i]]);
				ans = std::max(ans, j + B[i]);
			}
		}
	}
	std::cout << ans << std::endl;
	return 0;
}
