#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>
#include <map>
#include <queue>
#include <stack>
#define rep(i,n) for(int i=0;i<n;++i)

using namespace std;
int n, k, g, A[1010], B[1010], C[1010];
string s;
int main() {
	cin >> n;
	rep(i, n) {
		cin >> A[i];
		if (A[i] % 2 == 0) {
			B[g] = A[i];
			g++;
		}
		else {
			C[k] = A[i];
			k++;
		}
	}
	sort(B, B + g);
	sort(C, C + k);
	rep(i, k) cout << C[i] << " ";
	rep(i, g) {
		if (i != g - 1)
			cout << B[i] << " ";
		else
			cout << B[i] << endl;
	}
	return 0;
}
