//initを必ず実行
struct BIT {
	int bit[505050];
	void init() {
		REP(i, 505050)bit[i] = 0;
	}
	int sum(int i) {
		int s = 0;
		while (i>0) {
			s += bit[i];
			i -= i& -i;
		}
		return s;
	}
	void add(int i, int x) {
		while (i <= 505050) {
			bit[i] += x;
			i += i& -i;
		}
	}
};
BIT bit;
int q;
//x番目の数を返す
int calc(int num) {
	int left = -1, right = 505000;
	while (right - left > 1) {
		int mid = (left + right) / 2;
		if (bit.sum(mid) < num) {
			left = mid;
		}
		else {
			right = mid;
		}
	}
	return right;
}

int main() {
