#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
int main(){
    int n;
    std::cin>>n;
    std::vector<int> a(n);
    for(int i=0;i<n;++i){
        std::cin>>a[i];
    }
    std::sort(a.begin(),a.end());
    for(int i=0;i<n;++i){
        if(a[i]%2==1){
            std::cout<<a[i]<<" ";
        }
    }
    for(int i=0;i<n;++i){
        if(a[i]%2==0){
            std::cout<<a[i]<<" ";
        }
    }
    std::endl;
}
