#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>

int main() {
	std::string s;
	std::cin >> s;

	for (int i = 0; i < s.size(); i++) {
		if (s[i] >= 'a') {
			char x = s[i] - ('a' - 'A');
			std::cout << x;
		}
		else {
			char x = s[i] + ('a' - 'A');
			std::cout << x;
		}
	}
	std::cout << std::endl;
}
