#include<iostream>
#include<cstring>
using namespace std;
int main(){
    string a;
    cin>> a;
    for(int i=0;i<a.length();i++){
        if(a[i]<0x61){
            a[i]+=0x20;
        }else if(a[i]>0x41){
            a[i]=a[i]-0x20;
        }
    }
    for(int i=0;i<a.length();i++){
        cout<<a[i];
    }
    return 0;
    cout<<endl;
}
