#include<string>
#include<cmath>
#include<utility>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
typedef long long ll;

int main() {
	std::string str;
	std::cin >> str;
	rep(i, str.length()) {
		if (str[i] >= 65 && str[i] <= 90) {
			str[i] = str[i] + 32;
		}else{
			str[i] = str[i] - 32;
		}
	}
	std::cout << str << std::endl;
}
