#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <algorithm>

char tolower(char c) {
	return (c + 0x20);
}

/* 小文字を大文字に変換 */
char toupr(char c) {
	return (c - 0x20);
}

int main() {
	std::string str, tsr;
	std::cin >> str;
	tsr = str;
	for (int i = 0; i < str.size(); i++) {
		if ('a' <= str[i] && str[i] <= 'z')tsr[i] = toupr(str[i]);
		else if ('A' <= str[i] && str[i] <= 'Z')tsr[i] = tolower(str[i]);
	}
	std::cout << tsr << std::endl;
	return 0;
}
