#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>

int main()
{
	int n, input;
	std::vector<int> a, b;
	std::cin >> n;
	for(int i = 0; i < n; i++){
		std::cin >> input;
		if(input % 2 == 0){
			b.push_back(input);
		}else{
			a.push_back(input);
		}
	}
	std::sort(a.begin(),a.end());
	std::sort(b.begin(),b.end());
	for(int i = 0; i < a.size(); i++){
		std::cout << a[i];
		if(i != n - 1) std::cout << " ";
	}
	for(int i = 0; i < b.size(); i++){
		std::cout << b[i];
		if(i != n - a.size() - 1) std::cout << " ";
	}
	std::cout << std::endl;
	return 0;
}
