#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <cmath>
using namespace std;
int main() {
	string s;
	cin >> s;
	int y = s.size();
	for (int i = 0;i < y;i++) {
		if (tolower(s[i]) != s[i]) {
			s[i] = s[i] + 32;
		}
		else {
			s[i] = s[i] - 32;
		}
	}

	cout << s << endl;
}
