#include<iostream>

int main()
{
	int n;

	std::cin >> n;

	for (int i = 1; i <= n; i++) {
		std::cout << i << "は";
		if (i % 15 == 0)std::cout << "FizzBuzz";
		else if (i % 5 == 0)
			std::cout << "Fizz";

		else if (i % 3 == 0)
			std::cout << "Buzz" << std::endl;
	}




	std::cout << "です\n";
}

return 0;

}
