#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <algorithm>


int main() {
	int N;
	std::vector<int>k, g;
	std::cin >> N;
	for (int i = 0; i < N; i++) {
		int a;
		std::cin >> a;
		if (a % 2 == 1) {
			k.push_back(a);
		}
		else {
			g.push_back(a);
		};
	}
	std::sort(k.begin(), k.end());
	std::sort(g.begin(), g.end());
	for (int i = 0; i < k.size(); i++) {
		std::cout << k[i] << " ";
	}
	for (int i = 0; i < g.size(); i++) {
		std::cout << g[i] << " ";
	}
	std::cout << std::endl;
	return 0;
}
