#include "iostream"
#include "string"
#include "vector"
#include "map"
#include "queue"
#include "stack"
#include "algorithm"

using namespace std;

int main() {
	int n;
	vector<int> k, g;
	cin >> n;
	for (int i = 0; i < n; i++) {
		int a;
		cin >> a;
		if (a % 2 == 0) g.push_back(a);
		else k.push_back(a);
	}
	sort(g.begin(), g.end());
	sort(k.begin(), k.end());
	for (int i = 0; i < k.size(); i++) {
		cout << k[i];
		if (i != k.size() - 1) cout << ' ';
	}
	if (g.size() == 0) cout << ' ';
	for (int i = 0; i < g.size(); i++) {
		cout << g[i];
		if (i != g.size() - 1) cout << ' ';
	}
	cout << endl;

	return 0;
}
