#include<iostream>

int main() {
	int n;
	std::cin >> n;
	int a[100];
	for (int i = 0; i < n; i++) {
		std::cin >> a[i];
	}
	int kisuu[100] = {};
	int guusuu[100] = {};
	int kisuukazu = 0;
	int guusuukazu = 0;
	for (int i = 0; i < n; i++) {
		if (a[i] % 2 == 1) {
			for (int j = 0; j < n; j++) {
				if (kisuu[j] == 0) {
					kisuu[j] = a[i];
					kisuukazu++;
					break;
				}
			}
		}
		else {
			for (int j = 0; j < n; j++) {
				if (guusuu[j] == 0) {
					guusuu[j] = a[i];
					guusuukazu++;
					break;
				}
			}
		}
	}
	int c;
	bool flug = true;
	while (flug) {
		flug = false;
		for (int i = 0; i < kisuukazu - 1; i++) {
			if (kisuu[i] > kisuu[i + 1]) {
				c = kisuu[i];
				kisuu[i] = kisuu[i + 1];
				kisuu[i + 1] = c;
				flug = true;
			}
			
		}
	}
	flug = true;
	while (flug) {
		flug = false;
		for (int i = 0; i < guusuukazu - 1; i++) {
			if (guusuu[i] > guusuu[i + 1]) {
				c = guusuu[i];
				guusuu[i] = guusuu[i + 1];
				guusuu[i + 1] = c;
				flug = true;

			}
		}
	}
	for (int i = 0; i < kisuukazu; i++) {
		std::cout << kisuu[i] << ' ';
	}
	for (int i = 0; i < guusuukazu-1; i++) {
		std::cout << guusuu[i] << ' ';
	}
	std::cout << guusuu[guusuukazu - 1] << std::endl;
}
