#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>
#include <map>
#include <queue>
#include <stack>
#define rep(i,n) for(int i=0;i<n;++i)

using namespace std;
int h = 0, m = 0, r = 0, l = 1;
string s;
int main() {
	cin >> s;
	rep(i, s.size()) {
		if (s[i] == 'r') {
			r = 1;
			l = 0;
		}
		else if (s[i] == 'l') {
			r = 0;
			l = 1;
		}
		else if (s[i] == 'u') {
			if (r == 1) {
				if (m != 59) m++;
				else m = 0;
			}
			else {
				if (h != 23) h++;
				else h = 0;
			}
		}
		else {
			if (r == 1) {
				if (m != 0) m--;
				else m = 59;
			}
			else {
				if (h != 0) h--;
				else h = 23;
			}
		}
	}
	cout << h << " " << m << endl;
	return 0;
}
