using namespace std;

vector<int> v;
int main() {
	bool sorted = false;
	int n, m = 0;
	cin >> n;
	string s;
	unsigned int k;
	rep(i, n) {

		cin >> s >> k;
		if (s == "ADD") {
			v.emplace_back(k);
			sorted = false;
		}
		if (s == "DELETE") {
			if (!sorted) {
				std::sort(v.begin(), v.end());
			}
			if (v.size() - m >= k) {
				v[k - 1] = 5000009;
				sorted = false;
				m++;
			}
		}
		if (s == "OUTPUT") {
			if (!sorted) {
				std::sort(v.begin(), v.end());
			}
			if (v.size() - m < k) {
				cout << "Not Exist!" << endl;
			}
			else {
				cout << v[k - 1] << endl;
			}
		}
	}
	return 0;
}
