#include<iostream>
#include<string>
using namespace std;

int main(){
	string str;
	cin >> str;
	int M_point = 0;
	bool clear = false;
	for (int i = 0; i < 20000; i++){
		M_point=str.find("M", i);
		if (M_point == -1)break;
		if (str.find("G", M_point) == M_point + 1 || str.find("AG", M_point) == M_point + 1){
			if (str.find("G", M_point) == M_point + 1)M_point += 1;
			if (str.find("AG", M_point) == M_point + 1)M_point += 2;
			if (str.find("R", M_point) == M_point + 1 || str.find("ER", M_point) == M_point + 1 || str.find("AER", M_point) == M_point + 1){
				clear = true;
				break;
			}
		}
		i = M_point;
	}
	if (clear)cout << "YES" << endl;
	else cout << "NO" << endl;
}
