#include<iostream>
#include<string>

std::string str;

int t;

int r;

enum tmtm{hour,minute};

tmtm select=hour;

int main(){
    
    std::cin>>str;
    
    for(auto&& x:str){
        if(x=='u'){
            if(select==hour)
                t+=60;
            else
                t++;
        }
        if(x=='d'){
            if(select==hour)
                t-=60;
            else
                t--;
        }
        if(x=='r')
            select=minute;
        if(x=='l')
            select=hour;
    }
    
    t=(t+1440*10000)%1440;
    
    std::cout<<t/60<<':'<<t%60<<std::endl;
    
    std::cout<<t;
    
}
