#include "iostream"
#include "string"
#include "vector"
#include "map"
#include "queue"
#include "stack"
#include "algorithm"

using namespace std;

int main() {
	string s;
	cin >> s;
	int h = 0, m = 0;
	bool hf = true;
	for (int i = 0; i < s.size(); i++) {
		switch (s[i]) {
		case 'u':
			if (hf) {
				h++;
			}
			else {
				m++;
			}
			break;

		case 'd':
			if (hf) {
				h--;
			}
			else {
				m--;
			}
			break;

		case 'r':
			hf = false;
			break;

		case 'l':
			hf = true;
			break;
		}
		if (h < 0) h += 24;
		if (m < 0) m += 60;
	}

	h %= 24;
	m %= 60;

	cout << h << ' ' << m << endl;

	return 0;
}
