#include <iostream>
#include <algorithm>
#include <cmath>
#include <string>
#include <vector>
#include <queue>
typedef unsigned long long ULL;

 int main (){

	 int n;
	 std::vector <int> a;
	 std::vector <int> odd;
	 std::vector <int> even;
	 std::cin >> n;

	 for (int i = 0; i < n; ++i) {
		 int x;
		 std::cin >> x;
		 a.push_back(x);
	 }
	 for (int i = 0; i < n; ++i) {
		 if (a[i] % 2 == 0) {
			 odd.push_back(a[i]);
		 }
		 else {
			 even.push_back(a[i]);
		 }
	 }

	 std::sort(even.begin(), even.end());
	 std::sort(odd.begin(), odd.end());

	 for (int i = 0; i < even.size(); ++i) {
		 std::cout << even[i] << " ";
	 }
	 for (int i = 0; i < odd.size(); ++i) {
		 std::cout << odd[i] << " ";
	 }
	 return 0;
}
