#include <string>
#include <algorithm>
#include <iostream>
using namespace std;


int main() {
	int a[10000];
	int b[10000];
	int c[20000];
	int n,A,B;
	A = 0;
	B = 0;
	cin >> n;
	for (int i = 0; i < n; i++) {
		cin >> c[i];
	}
	for (int i = 0; i < n; i++) {
		if (c[i] % 2 == 1) {
			a[A] = c[i];
			A++;
		}
		else {
			b[B] = c[i];
			B++;
		}
	}

	for (int i = 0; i < A - 1; i++) {
		for (int j = A - 1; j > i; j--) {
			if (a[j - 1] > a[j]) {
				int	temp = a[j];
				a[j] = a[j - 1];
				a[j - 1] = temp;
			}
		}
	}
	for (int i = 0; i < B - 1; i++) {
		for (int j = B - 1; j > i; j--) {
			if (b[j - 1] > b[j]) {
				int	temp = b[j];
				b[j] = b[j - 1];
				b[j - 1] = temp;
			}
		}
	}

	for (int i = 0; i < A; i++) {
		cout << a[i] << endl;
	}
	for (int i = 0; i < B; i++) {
		cout << b[i] << endl;
	}
	//A=奇数の数　B=偶数の数　a＝偶数の配列　b＝奇数の配列

}
