#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>

int main()
{
	std::string str;
	int hour=0, minute=0, select = 0;
	std::getline(std::cin, str);
	for(int i = 0; i < str.size(); i++){
		if(str[i]== 'u'){
			if(select == 0){
				hour++;
			}else{
				minute++;
			}
		}
		if(str[i]== 'd'){
			if(select == 0){
				hour--;
			}else{
				minute--;
			}
		}
		if(str[i]=='r'){
			select = 1;
		}
		if(str[i]=='l'){
			select = 0;
		}
	}
	hour %= 24;
	minute %= 60;
	if(hour < 0) hour += 24;
	if(minute < 0) minute += 60;
	std::cout << hour << ' ' << minute << std::endl;
	return 0;
}
