#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>

int main() {
	int a[100], x;
	int k[100], g[100];
	int p = 0, q = 0;
	std::cin >> x;
	for (int i = 0; i < x; i++) {
		std::cin >> a[i];
		if (a[i] % 2 == 1) {
			k[p] = a[i];
			p++;
		}
		else {
			g[q] = a[i];
			q++;
		}
	}
	std::sort(k, k + p);
	std::sort(g, g + q);

	for (int i = 0; i < p; i++) {
		std::cout << k[i] << " ";
	}
	for (int i = 0; i < q; i++) {
		if (i == q - 1) {
			std::cout << g[i];
		}
		std::cout << g[i] << " ";
	}
	std::cout << std::endl;
}
