#include<iostream>
#include<string>

std::string str;

int tttt;

enum tmtm{hour,minute};

tmtm select=hour;

int main(){
    
    std::cin>>str;
    
    for(auto&& x:str){
        if(x=='u'){
            if(select==hour)
                tttt+=60;
            else
                tttt++;
        }
        if(x=='d'){
            if(select==hour)
                tttt-=60;
            else
                tttt--;
        }
        if(x=='r')
            select=minute;
        if(x=='l')
            select=hour;
    }
    
    tttt=(tttt+1440*10000)%1440;
    
    std::cout<<tttt/60<<':'<<tttt%60<<std::endl;
    
}
