#define eb emplace_back
#define fr first
#define sc second

struct BIT {
private:
	vector<ll>Bit;
	ll N;
public:
	void init(ll n) {
		N = n;
		Bit.resize(n);
	}
	void add(ll a, ll w) {
		for (int x = a; x < N; x += x & -x) Bit[x] += w;
	}
	int SUM(ll a) {
		ll ret = 0;
		for (int x = a; x > 0; x -= x & -x) ret += Bit[x];
		return ret;
	}
};


ll q, x;
string s;

int main() {
	cin >> q;
	BIT bit;
	bit.init(500010);
	for (int i = 0;i < q;i++) {
		cin >> s;
		if (s == "ADD") {
			cin >> x;
			bit.add(x, 1);
		}
		else if (s == "OUTPUT") {
			cin >> x;
			ll l = 0, r = 500005,mid;
			while (r - l > 1) {
				mid = (r + l) / 2;
				if (bit.SUM(mid) >= x)r = mid;
				else l = mid;
			}
			if (r == 500005) {
				cout << "Not Exist!" << endl;
			}
			else {
				cout << r << endl;
			}
		}
		else {
			cin >> x;
			ll l = 0, r = 500005, mid;
			while (r - l > 1) {
				mid = (r + l) / 2;
				if (bit.SUM(mid) >= x)r = mid;
				else l = mid;
			}
			if (r == 500005)continue;
			bit.add(r, -1);
		}
	}
	return 0;
}
