#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
#include <stdio.h>
#include <sstream>
#define MAX 100

//#define SIZE_OF_ARRAY(array) (sizeof(array)/sizeof(array[0]))

int main() {
	std::string a;
	std::cin >> a;
	char* numstr;
	char* numstr2;
	int sentaku = 0;
	int aa = 0, b = 0;
	for (int i = 0; i <= int(a.size()); i++) {
		if (a[i] == 'u') {
			if (sentaku == 0) {
				if (aa == 23) {
					aa = 0;
				}
				else {
					aa++;
				}
			}
			else {
				if (b == 59) {
					b = 0;
				}
				else {
					b++;
				}
			}
		}
		else if (a[i] == 'd') {
			if (sentaku == 0) {
				if (aa == 0) {
					aa = 23;
				}
				else {
					aa--;
				}
			}
			else {
				if (b == 0) {
					b = 59;
				}
				else {
					b--;
				}
			}
		}
		else if (a[i] == 'r') {
			sentaku = 1;
		}
		else if (a[i] == 'l') {
			sentaku = 0;
		}
	}
	std::ostringstream oss ,os2;
	oss << aa;
	os2 << b;
	std::cout << oss.str() << " " << os2.str() << std::endl;
}
